		// TODO Auto-generated method stub
		public class Circle2{
			private double radius;
			public Circle2() {
				this.radius=0.0;
			}
			public double  getRadius() {
				return this.radius;
			}
			public void setRadius(double radius) {
				this.radius=radius;
			}
			public void area() {
				double a=3.1419*radius*radius;
				System.out.println("Area="+a);
			}
		}



